memberdef.initializer:
do {                                                     \
    if (X) {                                             \
        FAISS_THROW_FMT("Error: '%s' failed: " MSG, #X); \
    }                                                    \
} while (false)

do {                                                                  \
    if (!(X)) {                                                       \
        FAISS_THROW_FMT("Error: '%s' failed: " FMT, #X, __VA_ARGS__); \
    }                                                                 \
} while (false)
